// What is $(document).ready ? See: http://flowplayer.org/tools/documentation/basics.html#document_ready
$(document).ready(function() {

div = document.getElementById('home_main');		
		
if (div){
	// home_main vertical scroll
	$("#home_main").scrollable({
	
		// basic settings
		vertical: true,
		circular: true,
		speed: 4000,
	
		// up/down keys will always control this scrollable
		// keyboard: 'static',
	
		// assign left/right keys to the actively viewed scrollable
		onSeek: function(event, i) {
			horizontal.eq(i).data("scrollable").focus();
		}
	
	// home_main navigator (thumbnail images)
	}).navigator("#home_main_navi").autoscroll(15000);
	
	// horizontal scrollables. each one is circular and has its own navigator instance
	var horizontal = $(".scrollable").scrollable({ circular: true }).navigator(".home_navi");
	
	
	// when home_page loads setup keyboard focus on the first horzontal scrollable
	horizontal.eq(0).data("scrollable").focus();
	}
});
